require(exports => {

    function constructMessage(fields, msg, level) {
        if (msg) {
            return level + ' ' + fields.time + ' ' + msg;
        } else {
            return fields;
        }
    }

    exports.createLogger = () => {
        return {
            fatal: (fields, msg) => console.error(constructMessage(fields, msg, 'FATAL')),
            error: (fields, msg) => console.error(constructMessage(fields, msg, 'ERROR')),
            warn: (fields, msg) => console.warn(constructMessage(fields, msg, 'WARN')),
            info: (fields, msg) => console.info(constructMessage(fields, msg, 'INFO')),
            debug: (fields, msg) => console.debug(constructMessage(fields, msg, 'DEBUG')),
            trace: (fields, msg) => console.trace(constructMessage(fields, msg, 'TRACE'))
        }
    };
    exports.levelFromName = [];
});