(function () {
    'use strict';

    window.app = {
        getVersion: () => {
            var version = Windows.ApplicationModel.Package.current.id.version;
            return version.major + '.' + version.minor + '.' + version.build
        },
        getPath: () => 'ms-appdata:///local',
        setAppUserModelId: () => { },
        getLocale: () => Windows.Globalization.ApplicationLanguages.languages[0]
    };
})();