(function () {
    function Buffer(data) {
        this.data = data;
        this.toString = returnType => {
            switch (returnType) {
                case 'hex':
                    return window.crypto.hexFromBytes(this.data);
                case 'base64':
                    return btoa(this.data);
            }
        };
    }
    //window.Buffer = {
    //    from: data => new Buffer(data),
    //    isBuffer: buffer => buffer instanceof Buffer,
    //}
    window.Buffer = Buffer;
    window.Buffer.from = data => new Buffer(data);
    window.Buffer.isBuffer = buffer => buffer instanceof Buffer;
})();