'use strict';

(function () {
    window.path = {
        join: function () {
            return Array.prototype.slice.call(arguments).join('/');
        },
        normalize: path => path,
        relative: (dir, file) => file.replace(dir + '/',''),
        basename: path => {
            if (path.includes('/')) {
                return path.split('/').pop();
            } else if (path.includes('\\')) {
                return path.split('\\').pop();
            } else {
                return path;
            }
        }
    }
    window.__dirname = 'ms-appx://';
})();