function require(input) {
    if (typeof input === 'string') {
        const parts = input.split('/');
        const module = parts.pop();

        if (require.modules[module]) {
            const exports = {};
            require.modules[module](exports);
            return exports;
        } else {
            return window[module];
        }
    } else {
        const err = new Error();
        const start = err.stack.indexOf('at Global code (')+16;
        const end = err.stack.indexOf(':1:1)');
        const path = err.stack.substring(start,end);
        const parts = path.split('/');
        const file = parts.pop();
        const module = file === 'index.js' ? parts.pop() : file.replace('.js','');
        require.modules[module] = input;
    }
}
require.modules = {};