(function () {

    function transform(path) {
        return path.replace('ms-appx:\\','ms-appx://').replace('ms-appdata:\\','ms-appdata:///').replace(/\\/g,'/');
    }

    window.path.join2 = window.path.join;
    window.path.join = (...args) => transform(window.path.join2(...args));

    window.path.normalize2 = window.path.normalize;
    window.path.normalize = (...args) => transform(window.path.normalize2(...args));
})();